import { createSlice } from "@reduxjs/toolkit";
import axios from "axios"

export const STATUS = Object.freeze({
    IDLE: 'idle',
    ERROR: 'error',
    LOADING: 'loading',
    // PRODUCT_DETAILS_REQUEST,
    // PRODUCT_DETAILS_SUCCESS,
    // PRODUCT_DETAILS_FAIL,
});

const UserSlice = createSlice({
    name:"user",
    initialState:{
        status: STATUS.ERROR,
        userDetail : {},
    },
    reducers:{
        getStatus(state,action){
            state.status = action.payload;
        },
        getUserDetail(state,action){
            state.userDetail = action.payload
        }


    }
})

export default UserSlice.reducer;

export const {getStatus , getUserDetail  } = UserSlice.actions


export function RegisterReducer(obj){
    console.log("slice" , obj); 
    return async function RegisterThunk(dispatch , thunk) {

        dispatch(getStatus(STATUS.LOADING))

        // const config = { headers : { "Content-Type" : "application/json"}};
        try{


            // const {data} = await axios.post(`/api/login`, { email , password} , config)
            const {data} = await axios.post(`/api/register`, {obj})
            console.log(data)
            dispatch(
                getStatus(STATUS.IDLE),
            )
            dispatch(
                getUserDetail(data),
            )
        }catch(error){
            dispatch (getStatus(STATUS.ERROR))
        }
    }
}


